.footer {
    background-color: $red;
    color: $white;
    padding: 1em;

    &__info {



        @include small{
            // https://developer.mozilla.org/fr/docs/Web/CSS/:nth-of-type
            // permet de cibler le 3e element qui a la classe .footer__item
            &:nth-of-type(3) {
                border: none;
                margin-bottom: 2em;
            }
        }

        &-icon {
            color: $white;
            padding-right: 1em;
            text-align: right;
            width: 15%;
        }

        &-text h3 {
            font-family: $font-main;
            margin-bottom: 1em;
            font-weight: bold;
        }
    }

    &__links {

        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        margin-bottom: 2em;

        &__item {
            margin-bottom: 1em;
            padding: 0 1em;
            text-align: center;
            width: 50%;

            @include large {
                width: auto;
            }

            a {
                color: $white;
            }
        }
    }

    .social {
        margin-bottom: 1em;

        &__links {
            display: flex;
            justify-content: center;

            li {
                display: inline-block;
                margin: 0 1em;

                a {
                    color: $white;
                }
            }
        }
    }
}

